function hitungUmur(tgl, bln, thn) {
    // Mendapatkan tanggal hari ini
    const hariIni = new Date();
    
    // Membuat tanggal lahir
    const tanggalLahir = new Date(thn, bln - 1, tgl); // Bulan dikurangi 1 karena bulan dalam JavaScript dimulai dari 0
    
    // Menghitung perbedaan dalam milidetik
    let bedaMs = hariIni - tanggalLahir;
    
    // Mengonversi milidetik menjadi tahun
    const msPerYear = 1000 * 60 * 60 * 24 * 365.25; // Termasuk tahun kabisat
    let umur = bedaMs / msPerYear;
    
    // Pembulatan ke bawah untuk mendapatkan umur dalam tahun
    umur = Math.floor(umur);
    
    return umur;
}

